    this.loteService = loteService;
    this.spinner = spinner;
    this.toastr = toastr;
    this.activatedRouter = activatedRouter;
    this.router = router; // Se o botão novo for clicado carrega o form e o

    this.estadoSalvar = 'post'; // inicia a variavel com um obj vazio do tipo Evento

    this.evento = {}; // Passa a conf para o serviço do DatePcker configurar o portuges.
    // Passa a conf para o serviço do DatePcker configurar o portuges.

    this.localeService.use('pt-br');
  }

  get modoEditar() {
    return this.estadoSalvar == 'put';
  } // Retorna o comando Form.control


  get f() {
    return this.form.controls;
  } // faz as configurações do DatePicker


  get dpConfig() {
    return {
      isAnimated: true,
      adaptivePosition: true,
      dateInputFormat: 'DD/MM/YYYY hh:mm a',
      containerClass: 'theme-default',
      showWeekNumbers: false,
      showTodayButton: true
    };
  }

  resetForm() {
    this.form.reset();
  }

