        },
        error: error => {
          this.spinner.hide();
          this.toastr.error('Erro ao tentar carregar Evento', 'Erro!');
          console.log(error);
        },
        complete: () => this.spinner.hide()
      });
    }
  }

  salvarEvento() {
    this.spinner.show(); // Verifica se o formulario é valido e usando o spread (...) copia
    // usando o spread substitui todo o valor de evento, até o id.

    if (this.form.valid) {
      this.evento = this.estadoSalvar == 'post' ? Object.assign({}, this.form.value) : Object.assign({
        id: this.evento.id
      }, this.form.value); // passa o estado como post

      this.eventoService[this.estadoSalvar](this.evento).subscribe(eventoRetorno => {
        this.toastr.success(`Evento salvo com sucesso`, `Sucesso!`); // Faz o reload do caminho editar e disponibiliza a opção cadastrar lote

        this.router.navigate([`eventos/detalhe/${eventoRetorno.id}`]);
      }, error => {
        console.error(error);
        this.spinner.hide();
        this.toastr.error(`Erro ao salvar o evento `, `Erro!`);
      }, () => this.spinner.hide());
    }
  }

  salvarLotes() {
    this.spinner.show();

    if (this.form.controls.lotes['_asyncValidationSubscription.valid']) {
      this.loteService.saveLote(this.eventoId, this.form.value.lotes).subscribe(() => {
        this.toastr.success('Loles salvos com Sucesso!', 'Sucesso!'); //this.lotes.reset();
      }, error => {
        this.toastr.error('Erro ao tentar salvar lotes', ' Error!');
        console.error(error);
      }).add(() => this.spinner.hide());
