export class EventoDetalheComponent {
    constructor(fb, localeService, eventoService, loteService, spinner, toastr, activatedRouter, router) {
        //private router: ActivatedRoute => p/usado para pegar a rota/com id
        this.fb = fb;
        this.localeService = localeService;
        this.eventoService = eventoService;
        this.loteService = loteService;
        this.spinner = spinner;
        this.toastr = toastr;
        this.activatedRouter = activatedRouter;
        this.router = router;
        // Se o botão novo for clicado carrega o form e o
        this.estadoSalvar = 'post';
        // inicia a variavel com um obj vazio do tipo Evento
        this.evento = {};
        // Passa a conf para o serviço do DatePcker configurar o portuges.
        // Passa a conf para o serviço do DatePcker configurar o portuges.
        this.localeService.use('pt-br');
    }
    get modoEditar() {
        return this.estadoSalvar == 'put';
    }
    // Retorna o comando Form.control
    get f() {
        return this.form.controls;
    }
    // faz as configurações do DatePicker
    get dpConfig() {
        return {
            isAnimated: true,
            adaptivePosition: true,
            dateInputFormat: 'DD/MM/YYYY hh:mm a',
            containerClass: 'theme-default',
            showWeekNumbers: false,
            showTodayButton: true
        };
    }
    resetForm() {
        this.form.reset();
    }
    ngOnInit() {
        this.carregarEvento();
        this.validationForm();
    }
    get lotes() {
        return this.form.get("lotes");
    }
    cssValidator(campoForm) {
        return { 'is-invalid': (campoForm === null || campoForm === void 0 ? void 0 : campoForm.errors) && (campoForm === null || campoForm === void 0 ? void 0 : campoForm.touched) };
    }
    validationForm() {
        this.form = this.fb.group({
            tema: ['',
                [Validators.required, Validators.minLength(5),
                    Validators.maxLength(50),],
            ],
            local: ['', [Validators.required, Validators.maxLength(30)]],
            dataEvento: ['', Validators.required],
            qtdPessoa: ['', [Validators.required, Validators.max(500)]],
            telefone: ['', Validators.required],
            email: ['', [Validators.required, Validators.email]],
            imagemUrl: ['', Validators.required],
            // Recebe um array fazio
            lotes: this.fb.array([]),
        });
    }
    adicionarLote() {
        // faz um cast pra FormArray   //(this.form.get('lotes') as FormArray).push(this.criarLote(lote));
        // this.lotes chama o get lotes
        //{ id: 0 } as Lote) primeiro item vai ser zero e os demais valores padrões
        this.lotes.push(this.criarLote({ id: 0 }));
        console.log(this.lotes);
    }
    criarLote(lote) {
        return this.fb.group({
            id: [lote.id],
            nome: [lote.nome, Validators.required],
            preco: [lote.preco, Validators.required],
            dataInicio: [lote.dataInicio],
            dataFim: [lote.dataFim],
            quantidade: [lote.quantidade, Validators.required],
        });
    }
    carregarEvento() {
        this.eventoId = +this.activatedRouter.snapshot.paramMap.get('id');
        console.log("Captura " + this.eventoId);
        if (this.eventoId !== null && this.eventoId !== 0) {
            this.estadoSalvar = 'put'; // put = Update, Atualizar
            this.spinner.show();
            // + converte para numerico
            this.eventoService.getEventoById(+this.eventoId).subscribe({
                // Object.assign({}, evento)} =>  para fazer uma copia do objeto ou para o objeto
                // ... (stred)
                next: (evento) => {
                    this.evento = Object.assign({}, evento),
                        this.form.patchValue(this.evento);
                },
                error: (error) => {
                    this.spinner.hide();
                    this.toastr.error('Erro ao tentar carregar Evento', 'Erro!');
                    console.log(error);
                },
                complete: () => this.spinner.hide(),
            });
        }
    }
    salvarEvento() {
        this.spinner.show();
        // Verifica se o formulario é valido e usando o spread (...) copia
        // usando o spread substitui todo o valor de evento, até o id.
        if (this.form.valid) {
            this.evento = (this.estadoSalvar == 'post')
                ? Object.assign({}, this.form.value) : Object.assign({ id: this.evento.id }, this.form.value);
            // passa o estado como post
            this.eventoService[this.estadoSalvar](this.evento).subscribe((eventoRetorno) => {
                this.toastr.success(`Evento salvo com sucesso`, `Sucesso!`);
                // Faz o reload do caminho editar e disponibiliza a opção cadastrar lote
                this.router.navigate([`eventos/detalhe/${eventoRetorno.id}`]);
            }, (error) => {
                console.error(error);
                this.spinner.hide();
                this.toastr.error(`Erro ao salvar o evento `, `Erro!`);
            }, () => this.spinner.hide());
        }
    }
    salvarLotes() {
        this.spinner.show();
        if (this.form.controls.lotes['_asyncValidationSubscription.valid']) {
            this.loteService.saveLote(this.eventoId, this.form.value.lotes).subscribe(() => {
                this.toastr.success('Loles salvos com Sucesso!', 'Sucesso!');
                //this.lotes.reset();
            }, (error) => {
                this.toastr.error('Erro ao tentar salvar lotes', ' Error!');
                console.error(error);
            }).add(() => this.spinner.hide());
        }
    }
